# function for jaccard similarity of edges sets
jaccard_edgeset_similarity <- function(G1, G2) {
  inter <- length(E(G1 %s% G2))
  un <- length(E(G1 %u% G2))

  if (un == 0) {
    0
  } else {
    inter/un
  }
}


